mapper.update:
update manager 
	set bm_approval = #{manager.bm_approval}
	where bm_id = #{manager.bm_id}

mapper.select:

select
    count(*)
from
    business_member
where
    bm_manager = #{bm_manager}
and
    bm_num = #{bm_num}
and
    bm_id = #{bm_id}

mapper.update:
UPDATE 
    business_member 
SET 
    bm_pw = #{bm_pw}
where
    bm_manager = #{bm_manager}
and
    bm_num = #{bm_num}
and
    bm_id = #{bm_id}
